mapper.update:
UPDATE POST
SET post_status = 2
WHERE post_seq = #{post_seq}
AND board_name = #{board_name}

mapper.select:
SELECT COUNT(*)
FROM POST
WHERE board_name = #{board_name}

mapper.update:
UPDATE POST
SET post_title = #{post_title},
    post_content = #{post_content}
WHERE post_seq = #{post_seq}
